mapper.select:
select

from d_menu
where 1 = 1

    AND p_code= #{pCode,jdbcType=VARCHAR}


    AND menu_code like CONCAT('%',#{menuCode},'%')


    AND menu_name like CONCAT('%',#{menuName},'%')


    AND create_time >= #{createTimeStart,jdbcType=TIMESTAMP}


    AND create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}
